dataSample.sql:
CREATE TABLE IF NOT EXISTS `PREFIX_leomanagewidget_backup` ( `id_leomanagewidget` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_group` int(10) unsigned NOT NULL, `id_shop` int(10) unsigned NOT NULL, `position` int(10) unsigned NOT NULL DEFAULT '0', `active` tinyint(1) unsigned NOT NULL DEFAULT '0', `key_widget` int(10) unsigned NOT NULL, `params` text, PRIMARY KEY (`id_leomanagewidget`,`id_shop`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leomanagewidget_column` ( `id_column` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_group` int(10) unsigned NOT NULL, `id_shop` int(10) unsigned NOT NULL, `position` int(10) unsigned NOT NULL DEFAULT '0', `active` tinyint(1) unsigned NOT NULL DEFAULT '0', `params` text, PRIMARY KEY (`id_column`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leomanagewidget_content` ( `id_content` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_column` int(10) unsigned NOT NULL, `id_shop` int(10) unsigned NOT NULL, `position` int(10) unsigned NOT NULL DEFAULT '0', `active` tinyint(1) unsigned NOT NULL DEFAULT '0', `key_widget` int(10) unsigned NOT NULL, `module_name` varchar(64) NOT NULL, `hook_name` varchar(64) NOT NULL, `type` int(1) unsigned DEFAULT NULL, `params` text, PRIMARY KEY (`id_content`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leomanagewidget_group` ( `id_leomanagewidget_group` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, `position` int(10) unsigned NOT NULL DEFAULT '0', `active` tinyint(1) unsigned NOT NULL DEFAULT '0', `hook_name` varchar(64) NOT NULL, `type` int(1) unsigned DEFAULT NULL, `params` text, PRIMARY KEY (`id_leomanagewidget_group`,`id_shop`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
