CREATE TABLE IF NOT EXISTS `PREFIX_leosliderlayer_groups` ( `id_leosliderlayer_groups` int(10) unsigned NOT NULL AUTO_INCREMENT, `title` varchar(255) NOT NULL, `id_shop` int(10) unsigned NOT NULL, `hook` varchar(64) NOT NULL, `active` tinyint(1) unsigned NOT NULL DEFAULT '1', `params` text NOT NULL, PRIMARY KEY (`id_leosliderlayer_groups`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leosliderlayer_slides` ( `id_leosliderlayer_slides` int(10) unsigned NOT NULL AUTO_INCREMENT, `id_group` int(11) NOT NULL, `position` int(10) unsigned NOT NULL DEFAULT '0', `active` tinyint(1) unsigned NOT NULL DEFAULT '1', `parent_id` int(11) NOT NULL, `params` text NOT NULL, PRIMARY KEY (`id_leosliderlayer_slides`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_leosliderlayer_slides_lang` ( `id_leosliderlayer_slides` int(10) unsigned NOT NULL, `id_lang` int(10) unsigned NOT NULL, `title` varchar(255) NOT NULL, `link` varchar(255) NOT NULL, `image` varchar(255) NOT NULL, `thumbnail` varchar(255) NOT NULL, `video` text, `layersparams` text, PRIMARY KEY (`id_leosliderlayer_slides`,`id_lang`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
